mapper.select:
SELECT u.name AS guest_name, u.phone AS guest_phone, rr.room_name, r.checkin_date, r.checkout_date, r.reservation_status, res.resid_no FROM reservations r JOIN residence_rooms rr ON r.room_no = rr.room_no JOIN users u ON r.user_no = u.user_no JOIN residence res ON rr.resid_no = res.resid_no WHERE res.resid_no = #{hotelId}
AND rr.room_name LIKE CONCAT('%', #{keyword}, '%')
AND r.checkin_date = #{keyword}
AND r.checkout_date = #{keyword}
